import type { NextConfig } from "next";

const withPWA = require("next-pwa")({
  dest: "public",
  disable: process.env.NODE_ENV === "development",
  register: false, // we register manually (more control / fewer surprises)
  skipWaiting: true,
  clientsClaim: true,
  cacheOnFrontEndNav: true, // cache pages during client-side navigation
  fallbacks: {
    document: "/offline", // show offline page when a page can't be fetched
  },
  // These runtime rules are in addition to the precache manifest generated by next-pwa.
  runtimeCaching: [
    // Sheet music PNGs (static assets served from /public/sheets/fil/*)
    {
      urlPattern: /\/sheets\/fil\/.*\.png$/i,
      handler: "CacheFirst",
      options: {
        cacheName: "sheet-images",
        expiration: {
          maxEntries: 5000,
          maxAgeSeconds: 60 * 60 * 24 * 365, // 1 year
        },
        cacheableResponse: {
          statuses: [0, 200],
        },
      },
    },
    // 1. Next.js RSC Data (for client-side link navigation)
    {
      urlPattern: ({ url, request }: { url: URL; request?: any }) => {
        const isRsc = url.search.includes("_rsc") ||
          (request && request.headers && request.headers.get && request.headers.get("RSC") === "1");
        return (url.origin === self.location.origin) && isRsc;
      },
      handler: "NetworkFirst",
      options: {
        cacheName: "pages-rsc",
        networkTimeoutSeconds: 3,
        expiration: {
          maxEntries: 1500,
          maxAgeSeconds: 60 * 60 * 24 * 30,
        },
        cacheableResponse: {
          statuses: [0, 200],
        },
        matchOptions: {
          ignoreSearch: true,
        },
      },
    },
    // 2. HTML Pages (for direct navigation / hard reload)
    {
      urlPattern: ({ url, request }: { url: URL; request?: any }) => {
        const p = url.pathname;
        const isRsc = url.search.includes("_rsc") ||
          (request && request.headers && request.headers.get && request.headers.get("RSC") === "1");
        const isPage = p === "" || p === "/" || p.startsWith("/hymn/") || p === "/offline";
        return (url.origin === self.location.origin) && isPage && !isRsc;
      },
      handler: "NetworkFirst",
      options: {
        cacheName: "pages-html",
        networkTimeoutSeconds: 3,
        expiration: {
          maxEntries: 1500,
          maxAgeSeconds: 60 * 60 * 24 * 30,
        },
        cacheableResponse: {
          statuses: [0, 200],
        },
        matchOptions: {
          ignoreSearch: true,
        },
      },
    },
    // 3. PWA Metadata
    {
      urlPattern: /\/(manifest\.webmanifest|manifest\.json|favicon\.ico|apple-icon.*)$/i,
      handler: "StaleWhileRevalidate",
      options: {
        cacheName: "pwa-metadata",
      },
    },
    // Next static assets
    {
      urlPattern: /\/_next\/static\/.*/i,
      handler: "CacheFirst",
      options: {
        cacheName: "next-static",
        expiration: {
          maxEntries: 500,
          maxAgeSeconds: 60 * 60 * 24 * 365,
        },
        cacheableResponse: {
          statuses: [0, 200],
        },
      },
    },
    // Other same-origin requests (keep conservative to avoid caching POSTs, etc.)
    {
      urlPattern: ({ url }: { url: URL }) => url.origin === self.location.origin,
      handler: "NetworkFirst",
      options: {
        cacheName: "same-origin",
        networkTimeoutSeconds: 5,
        expiration: {
          maxEntries: 200,
          maxAgeSeconds: 60 * 60 * 24 * 7,
        },
        cacheableResponse: {
          statuses: [0, 200],
        },
      },
    },
  ],
});

const nextConfig: NextConfig = {
  // next-pwa configures webpack internally. Next 16 enables Turbopack by default
  // and will error if a webpack config is present without a turbopack config.
  turbopack: {},
};

export default withPWA(nextConfig);
