import type { NextConfig } from "next";

const withPWA = require("next-pwa")({
  dest: "public",
  disable: process.env.NODE_ENV === "development",
  register: false, // we register manually (more control / fewer surprises)
  skipWaiting: true,
  clientsClaim: true,
  cacheOnFrontEndNav: true, // cache pages during client-side navigation
  fallbacks: {
    document: "/offline", // show offline page when a page can't be fetched
  },
  // These runtime rules are in addition to the precache manifest generated by next-pwa.
  runtimeCaching: [
    // 1. Unified Offline Content (Home, Hymns, Data, Manifest, Icons)
    {
      urlPattern: ({ url }: { url: URL }) => {
        const p = url.pathname;
        const isOrigin = url.origin === self.location.origin;
        const isPageOrData = p === "/" ||
          p.startsWith("/hymn/") ||
          p.startsWith("/offline") ||
          p.startsWith("/_next/data/") ||
          url.search.includes("_rsc") ||
          p.includes("manifest") ||
          p.includes("icon") ||
          p.includes("favicon");
        return isOrigin && isPageOrData;
      },
      handler: "StaleWhileRevalidate",
      options: {
        cacheName: "offline-hymnal",
        expiration: {
          maxEntries: 2000,
          maxAgeSeconds: 60 * 60 * 24 * 30, // 30 days
        },
        cacheableResponse: {
          statuses: [0, 200],
        },
        matchOptions: {
          ignoreSearch: true,
        },
      },
    },
    // 2. Sheet Music PNGs
    {
      urlPattern: ({ url }: { url: URL }) => {
        return url.origin === self.location.origin && url.pathname.includes("/sheets/fil/");
      },
      handler: "CacheFirst",
      options: {
        cacheName: "sheet-images",
        expiration: {
          maxEntries: 5000,
          maxAgeSeconds: 60 * 60 * 24 * 365, // 1 year
        },
        cacheableResponse: {
          statuses: [0, 200],
        },
      },
    },
    // 3. Next.js Static Assets (JS/CSS)
    {
      urlPattern: ({ url }: { url: URL }) => {
        return url.origin === self.location.origin && url.pathname.startsWith("/_next/static/");
      },
      handler: "CacheFirst",
      options: {
        cacheName: "next-static",
        expiration: {
          maxEntries: 500,
        },
      },
    },
    // 4. Everything Else (Conservative Fallback)
    {
      urlPattern: ({ url }: { url: URL }) => url.origin === self.location.origin,
      handler: "NetworkFirst",
      options: {
        cacheName: "general-fallback",
        networkTimeoutSeconds: 5,
        expiration: {
          maxEntries: 200,
        },
      },
    },
  ],
});

const nextConfig: NextConfig = {
  // next-pwa configures webpack internally. Next 16 enables Turbopack by default
  // and will error if a webpack config is present without a turbopack config.
  turbopack: {},
};

export default withPWA(nextConfig);
